INSERT INTO `ecs_goods` VALUES ('268', '174', 'ECS000268', '施华洛世奇(Swarovski)女包 Firework 包包 图片色', '+', '2', '59', '', '9999', '0.000', '3000.00', '2500.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;</p>\r\n<div>\r\n<div style=\"margin: auto; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; width: 990px;\">\r\n<h4 style=\"margin: 0px auto 15px; padding: 5px; background-color: rgb(0, 0, 0); width: 297px; text-align: center; color: rgb(255, 255, 255); font-weight: lighter; white-space: nowrap;\">DESIGNER DESCRIPTION</h4>\r\n</div>\r\n<div style=\"margin: 0px; padding: 10px 0px; color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; background-color: rgb(0, 0, 0);\">\r\n<h2 style=\"margin: 0px; padding: 0px; font-size: 14px; text-align: center; color: rgb(255, 255, 255);\">Swarovski/施华洛世奇</h2>\r\n<hr style=\"box-sizing: content-box; height: 0px; width: 594px;\" />\r\n<p style=\"margin: 40px; padding: 0px; color: rgb(153, 153, 153); text-align: justify; line-height: 1.75em;\">施华洛世奇 (Swarovski) 是世界上首屈一指的水晶制造商，每年为时装、首饰及水晶灯等工业提供大量优质的切割水晶石。同时施华洛世奇 (Swarovski) 也是以优质、璀璨夺目和高度精确的水晶和相关产品闻名于世的奢侈品品牌。施华洛世奇 (Swarovski) 产品为动人之处，不仅仅在于它们是多么巧妙地被打磨成数十个切面，以致其对光线有极好的折射能力，整个水晶制品看起来格外耀眼夺目，更在于施华洛世奇 (Swarovski) 一直通过其产品向人们灌输着一种精致文化。施华洛世奇 (Swarovski) 的魅力源自材料的品质和采用的制造方法。至于独特制法的详细情况，则不会向外人透露。施华洛世奇公司 (Swarovski AG) 这家古老而神秘的公司仍保持着家族经营方式，把水晶制作工艺作为商业秘密代代相传，独揽与水晶切割有关的专利和财富。施华洛世奇 (Swarovski) 高级定制系列创始于1989年，提供一系列精致瑰丽的首饰、手袋和配饰，同时见证者施华洛世奇 (Swarovski) 的精湛工艺。这些作品采用优质的物料、独特的皮革、珍贵的宝石和璀璨的水晶，是红地毯上名人巨星的闪烁点缀。1995年施华洛世奇 (Swarovski) 为庆祝成立100周年于发源地华登斯设立了水晶梦幻世界。水晶世界成为奥地利参观人数排名第二的旅游景点，开幕至今已吸引超过700万名水晶爱好者，成为水晶迷必到的朝圣地。1997年，施华洛世奇 (Swarovski) 首次踏足美国的首饰和时装业。在之后十多年的时间里，施华洛世奇 (Swarovski) 将业务扩展至英国及欧洲其他国家。令人惊喜的精致首饰，让时装迷和经典首饰爱好者钟情不已。施华洛世奇 (Swarovski)首饰亦成为现今时尚界备受赞誉的品牌之一。2002年，由娜佳&middot;施华洛世奇策划的水晶宫系列，自面世起就表现、展示和示范如何以惊人手法全新诠释传统吊灯。2007年，施华洛世奇 (Swarovski) 与电子业巨头飞利浦合作，生产出&ldquo;活动的水晶&ldquo; (Active-Crystals) 电子产品系列。这个系列的产品包括USB记忆卡和耳塞等。这些产品都有施华洛世奇 (Swarovski) 的水晶作为装饰。天鹅水晶标志是施华洛世奇 (Swarovski) 的传统记号，也是施华洛世奇 (Swarovski) 水晶精致优雅精神的象征。</p>\r\n</div>\r\n<div style=\"margin: auto; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; width: 990px;\">\r\n<h2 style=\"margin: 0px 0px 5px; padding: 0px; font-size: 14px; text-align: center; color: rgb(0, 0, 0);\">商家描述</h2>\r\n<h4 style=\"margin: 0px auto 15px; padding: 5px; background-color: rgb(0, 0, 0); width: 297px; text-align: center; color: rgb(255, 255, 255); font-weight: lighter; white-space: nowrap;\">RETAILOR DESCRIPTION</h4>\r\n</div>\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; font-size: medium;\">\r\n<p style=\"margin: 30px 30px -20px; padding: 0px; line-height: 1.6em; color: rgb(153, 153, 153); font-size: 20px;\">商品由美国品牌官网发货，下单即采购。</p>\r\n<ul style=\"margin: 30px 30px 20px; padding: 0px; list-style: none; line-height: 2em; color: rgb(51, 51, 51); font-size: 20px; font-weight: bold;\">\r\n    <li style=\"margin: 0px; padding: 0px;\">● 商品货号：<span style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-weight: normal;\">SW5240635\r\n    <p style=\"margin: 0px; padding: 0px;\">&nbsp;</p>\r\n    </span></li>\r\n    <li style=\"margin: 0px; padding: 0px;\">● 商品说明：</li>\r\n    <li style=\"margin: 0px; padding: 0px;\">● 尺寸特殊说明：<span style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-weight: normal; text-align: justify;\">国外的币种尺寸与人民币可能存在差异，请注意留意钱包尺寸，如果有疑问请联系在线客服。</span></li>\r\n</ul>\r\n</div>\r\n</div>', 'images/201707/thumb_img/268_thumb_G_1499733923195.jpg', 'images/201707/goods_img/268_G_1499733923318.jpg', 'images/201707/source_img/268_G_1499733923915.png', '1', '', '1', '1', '0', '0', '1499733882', '100', '0', '0', '0', '0', '0', '0', '1499733923', '0', '', '-1', '-1', '0', null, '0', '0', '', null, '0', '0', '', '0');
